import React from "react";
import Button from "react-bootsrap"

function BotItem({ bots, handleClicked, handleDelete }) {
    return (
        <>
          <h1>Battle Bots</h1>
          <div className="d-flex flex-wrap ">
            {bots.map((bot) => (
              <div key={bot.id} className="card" style={{ width: 18 + "em" }} onClick={handleClicked}>
                <h3 className="card-title">{bot.name}</h3>
                <img src={bot.avatar_url} className="card-img-top" alt={bot.name} />
                <div className="card-body">
                  <h4>{bot.bot_class}</h4>
                  <p>Health:{bot.health}</p>
                  <p>Damage:{bot.damage}</p>
                  <p>Armor:{bot.armor}</p>
                  <p className="card-text"> catchphrase:{bot.catchphrase}</p>
                  <Button onClick={()=>handleDelete()} className="btn btn-secondary">X</Button>
                </div>
              </div>
            ))}
          </div>
        </>
      );
}

export default BotItem;
