import React, { useEffect, useState } from "react";
import BotItem from "./BotItem";
import YourBotArmy from "./YourBotArmy";

function BotCollection() {
  const [bots, setBots] = useState([]);
  const [botId, setBotId] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/bots")
      .then((r) => r.json())
      .then((data) => {
        console.log(data);
        setBots(data);
      });
  }, []);

  function handleDelete(id) {
    fetch(`"http://localhost:3000/bots"/${id}`, {
      method: "DELETE",
    }).then(() => {
      setBots((bots) => bots.filter((bt) => bt.id !== id));
    });
  }
  console.log(botId);

  function handleClicked(idInput) {
    setBotId([...botId, idInput]);
  }

  // const botContent = bots.map((content) => (
  //   <BotItem
  //     image={content.avatar_url}
  //     name={content.name}
  //     health={content.health}
  //     damage={content.damage}
  //     armor={content.armor}
  //     bot_class={content.bot_class}
  //     catchphrase={content.catchphrase}
  //     id={content.id}
  //     handleClicked={handleClicked}
  //     handleDelete={handleDelete}
  //   />
  // ));

  return (
    <div>
      <YourBotArmy botId={botId} bots={bots} setBotId={setBotId} />
      <div className="container mt-4">
        <BotItem handleClicked={handleClicked} handleDelete={handleDelete} bots={bots}/>
      </div>
    </div>
  );
}

export default BotCollection;
