import React from "react";

function SelectedBot({ electedBots,handleDelete }) {
  return (
    <div className="col-3 p-1">
      {electedBots.map((bot)=>(
      <div
      className="card border-warning"
      key={bot.id}
      onClick={() => handleDelete(bot.id)}
    >
      <img className="card-img-top" src={bot.avatar_url} alt="news item" />
      <div className="card-body">
        <h3 className="card-title">{bot.name}</h3>
        <h4 className="card-title">{bot.bot_class}</h4>
        <p className="card-text">{bot.catchphrase}</p>
        <div>
          <p>Health: {bot.health} </p>
          <p>Damage: {bot.damage}</p>
          <p>Armor: {bot.armor}</p>
        </div>
      </div>
    </div>
      ))}

    </div>
  );
}

export default SelectedBot;
