import React from "react";

function SelectedBot({ electedBot, handleDelete }) {
  return (
    <div className="col-3 p-1">
      {electedBot.map((electedBot) => (
        <div
          className="card border-warning h-100"
          key={electedBot.id}
          onClick={() => handleDelete(electedBot.id)}
        >
          <img className="card-img-top" src={electedBot.avatar_url} alt="news item" />
          <div className="card-body">
            <h3 className="card-title">{electedBot.name}</h3>
            <h4 className="card-title">{electedBot.bot_class}</h4>
            <p className="card-text">{electedBot.catchphrase}</p>
            <div>
              <p>Health: {electedBot.health} </p>
              <p>Damage: {electedBot.damage}</p>
              <p>Armor: {electedBot.armor}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default SelectedBot;
