import React from "react";

function BotItem({ bots, handleClicked, handleDelete }) {
    console.log(bots)
  return (
    <div className="d-flex ">
      <h2>Available Bots</h2>
      {bots.map((bot) => (
        <div
          className="card border-warning h-100"
          onClick={() => handleClicked(bot.id)}
          key={bot.id}
        >
          <img className="card-img-top" src={bot.avatar_url} alt="news item" />
          <div className="card-body">
            <h3 className="card-title">{bot.name}</h3>
            <h4 className="card-title">{bot.bot_class}</h4>
            <p className="card-text">{bot.catchphrase}</p>
            <hr />
            <p>Health: {bot.health} </p> <p>Damage: {bot.damage}</p>{" "}
            <p>Armor: {bot.armor}</p>
            <center>
              <button
                type="button"
                class="btn btn-outline-danger"
                onClick={() => handleDelete(bot.id)}
              >
                X
              </button>
            </center>
          </div>
        </div>

     ))
     }
    </div> 
);
}


export default BotItem;
